INSERT INTO `faqs` VALUES (1, 'Apa itu maklon kosmetik & skincare?', 'Jasa maklon kosmetik dan skincare adalah sebuah fasilitas produksi yang mengkhususkan diri dalam memproduksi berbagai produk kecantikan, disesuaikan dengan ketentuan dan spesifikasi klien.', '2024-10-08 14:11:43', '2024-10-08 14:11:47');
INSERT INTO `faqs` VALUES (3, 'Berapa minimal order quantity di Dua Naga Kosmetindo?', 'Sebagai salah satu perusahaan jasa pembuatan produk kosmetik dan skincare yang berkomitmen mendukung UMKM, PT. Dua Naga Kosmetindo menawarkan MOQ yang sangat terjangkau, mulai dari 1.000 pcs/item. Dengan jumlah tersebut, Anda dapat memiliki produk kecantikan berkualitas dengan merek Anda sendiri.', NULL, NULL);
INSERT INTO `faqs` VALUES (4, 'Apakah Dua Naga Kosmetindo membantu proses pendaftaran BPOM, HAKI dan sertifikasi Halal?', 'Ya, PT. Dua Naga Kosmetindo membantu dalam proses pendaftaran BPOM, HAKI, dan sertifikasi Halal. Pendaftaran BPOM (Badan Pengawas Obat dan Makanan) Kami membantu klien dalam seluruh proses pendaftaran produk kosmetik ke BPOM, mulai dari persiapan dokumen, pengujian laboratorium, hingga pengajuan pendaftaran. Dengan bantuan kami, Anda dapat memastikan bahwa produk Anda memenuhi standar keamanan dan kualitas yang ditetapkan oleh BPOM, sehingga dapat beredar secara legal di pasaran. Pendaftaran HAKI (Hak Kekayaan Intelektual) Kami juga menyediakan layanan untuk pendaftaran merek, desain industri, dan hak cipta produk Anda. Tim kami akan membantu Anda dalam menyiapkan dokumen dan mengajukan pendaftaran ke Direktorat Jenderal Kekayaan Intelektual (DJKI). Pendaftaran HAKI melindungi produk Anda dari peniruan dan pelanggaran hak cipta, serta meningkatkan nilai brand Anda di mata konsumen. Sertifikasi Halal Kami bekerja sama dengan lembaga sertifikasi Halal untuk memastikan produk kosmetik Anda memenuhi standar halal. Kami akan membantu Anda dalam menyiapkan dokumen yang diperlukan, proses audit, dan pengajuan sertifikasi. Sertifikasi Halal memastikan bahwa produk Anda aman digunakan oleh konsumen Muslim dan dapat meningkatkan daya saing produk di pasar lokal maupun internasional.', NULL, NULL);
INSERT INTO `faqs` VALUES (5, 'Apakah Dua Naga Kosmetindo menyediakan wadah dan kemasan sekaligus desain?', 'Kami menyediakan berbagai jenis wadah dan kemasan dari supplier kemasan yang sudah bekerjasama dengan PT. Dua Naga Kosmetindo. seperti botol, jar, tube, dan pot yang terbuat dari bahan plastik, kaca, dan aluminium. Tersedia dalam berbagai ukuran dan desain sesuai dengan kebutuhan brand Anda. Selain kemasan, Tim desain kreatif kami siap membantu Anda menciptakan desain kemasan yang unik dan menarik, sesuai dengan identitas brand Anda.', NULL, NULL);
INSERT INTO `faqs` VALUES (6, 'Bolehkah saya mengunjungi kantor dan pabrik?', 'Tentu boleh, namun untuk kunjungan diharuskan untuk menginfokan kepada Customer Service kami melalui WhatsApp atau Telepon maksimal H-2 sebelum hari kunjungan.', NULL, NULL);
INSERT INTO `faqs` VALUES (7, 'Bolehkah saya membawa kemasan atau wadah sendiri?', 'Boleh. Namun, departemen pengendalian mutu kami akan menguji kualitasnya terlebih dahulu melalui Stability Test. Jika kami menemukan sesuatu yang tidak sesuai, kami akan meminta Anda untuk beralih ke wadah atau kemasan lain. Kami juga dapat memperkenalkan produsen wadah kepada Anda apabila diperlukan.', NULL, NULL);
INSERT INTO `faqs` VALUES (8, 'Bagaimana saya memulai bekerjasama dengan maklon Dua Naga Kosmetindo?', 'Untuk memulai kerjasama, hubungi kami melalui formulir kontak atau melalui nomor kontak yang tersedia di situs web kami. Tim kami akan segera menghubungi Anda untuk konsultasi awal.', NULL, NULL);
INSERT INTO `maklon_steps` VALUES (1, 'Konsultasi Konsep Produk', 'Diskusikan bersama kami konsep produk kosmetik yang sesuai dengan keinginan Anda. Jika Anda masih merasa ragu atau bingung mengenai konsep produk. Tim kami akan memberikan saran dari pemilihan formula, bahan baku kosmetik, tekstur, kemasan, hingga target pasar.', '1.png', '2024-10-10 15:27:17', '2024-10-10 15:27:17');
INSERT INTO `maklon_steps` VALUES (2, 'Pembuatan Sampel Produk', 'Setelah menentukan produk, formula, dan bahan baku yang telah disetujui bersama, kami akan membuatkan sampel produk. Pada tahap ini produk akan melalui uji stabilitas dan kompatibilitas.', '2.png', '2024-10-10 15:27:17', '2024-10-10 15:27:17');
INSERT INTO `maklon_steps` VALUES (3, 'Pembayaran Biaya Maklon', 'Setelah membuat kesepakatan, Anda akan melanjutkan ke proses pembayaran. Biaya maklon yang harus dibayarkan dimuka / DP (Down Payment) adalah sebesar 50 persen dari total pemesanan produk.', '3.png', '2024-10-10 15:27:17', '2024-10-10 15:27:17');
INSERT INTO `maklon_steps` VALUES (4, 'Legalitas Perizinan', 'Kami Memberikan service menyeluruh untuk pengurusan ijin/sertifikasi BPOM, HKI, HALAL hingga tuntas!', '4.png', '2024-10-10 15:27:17', '2024-10-10 15:27:17');
INSERT INTO `maklon_steps` VALUES (5, 'Desain Kemasan', 'Kami mempunyai tim Designer terbaik untuk menunjang produk kebanggaan Anda.', '5.png', '2024-10-10 15:27:17', '2024-10-10 15:27:17');
INSERT INTO `maklon_steps` VALUES (6, 'Proses Produksi', 'Percayakan proses produksi kepada kami, karena kami telah berstandarisasi CPKB-BPOM.', '6.png', '2024-10-10 15:27:17', '2024-10-10 15:27:17');
INSERT INTO `maklon_steps` VALUES (7, 'Quality Control', 'Setelah diproduksi, produk harus melewati proses kualitas kontrol untuk memenuhi standar kualitas yang sesuai dengan standar mutu yang telah ditetapkan.', '7.png', '2024-10-10 15:27:17', '2024-10-10 15:27:17');
INSERT INTO `maklon_steps` VALUES (8, 'Pengiriman Produk', 'Setelah proses produksi & Pengecekan selesai, kami akan membantu mengirimkan produk Anda yang siap dijual ke warehouse Anda.', '8.png', '2024-10-10 15:27:17', '2024-10-10 15:27:17');
