INSERT INTO `products` VALUES (19, 'Zinc Oxide Sunscreen Ultra Light Cream with SPF 50 For Broad Spectrum, UVA, UVB & Blue Light Protection', '<p><strong>Sensasi Pemkaiaan:&nbsp;</strong></p><ul><li>Seng oksida: Melindungi dari sinar UVA dan UVB dengan penghalang fisik, memantulkan dan menghamburkan sinar matahari untuk perlindungan spektrum luas.&nbsp;</li><li>Haver: Menenangkan dan menenangkan kulit sensitif dan teriritasi dengan senyawa anti-inflamasi.&nbsp;</li><li>alpukat: Melembabkan secara mendalam dengan asam lemak dan vitamin, menjaga keseimbangan kelembapan alami kulit.</li></ul><p><strong>Cara Pemakaian:</strong></p><ul><li>Oleskan gel tabir surya pada wajah bersih, leher, dan area terbuka lainnya. Tepuk-tepuk perlahan agar menyerap.&nbsp;</li></ul>', NULL, NULL, 1, 1, '<p><strong>Tekstur:</strong> Cream</p><p><strong>Jenis Kulit:</strong> Semua jenis kulit</p><p><strong>Digunakan untuk:</strong> Wajah</p><p><strong>Bahan Utama:</strong> <span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Zinc Oxide, Oat, Avocado</span></p><p><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\"><strong>Manfaat:</strong> Tabir surya fisik 100% dengan seng oksida ini dikembangkan untuk kulit sensitif. Teksturnya yang tidak memutihkan dan cepat menyerap memberikan hasil akhir matte pada kulit.</span></p>', 'zinc-oxide-sunscreen-ultra-light-cream-with-spf-50-for-broad-spectrum-uva-uvb-blue-light-protection', 98, '2024-10-15 13:09:59', '2025-01-05 02:54:08');
