INSERT INTO `products` VALUES (21, '4 Kit Facial Ultra Light Sunscreen Quick Dry Moisturizing Sunscreen SPF 50', '<p><strong>Sensasi Pemakaian:</strong></p><ul><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Zinc Oxide adalah tabir surya mineral yang menempel di kulit Anda. Ini menyebarkan, memantulkan, dan menyerap sinar UVA dan UVB yang berbahaya.&nbsp;</span><br>&nbsp;</li></ul><p><strong>Cara Pemakaian:</strong></p><ul><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Oleskan secara merata dan merata 15 menit sebelum paparan sinar matahari.&nbsp;</span></li><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Oleskan kembali setidaknya setiap 2 jam atau setelah 80 menit berenang atau berkeringat.</span></li></ul>', NULL, NULL, 1, 1, '<p><strong>Tekstur:</strong> Cream</p><p><strong>Jenis Kulit:</strong> Semua jenis kulit</p><p><strong>Digunakan untuk:</strong> Wajah dan Badan</p><p><strong>Bahan Utama:</strong> <span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Zinc Oxide, Vitamin E, Aloe</span></p><p><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\"><strong>Manfaat:</strong> Tabir surya SPF 50+ yang menyerap cepat membantu melindungi kulit Anda dari kerusakan akibat sinar matahari dengan perlindungan spektrum luas UVA/UVB yang sangat tinggi. Diformulasikan khusus untuk wajah dan tubuh dengan Vitamin E dan Aloe Vera yang menutrisi kulit, formulanya menghidrasi tanpa terasa berminyak, diaplikasikan dengan lembut, dan nyaman dipakai. Tahan air hingga 4 jam. Ukurannya yang nyaman membuatnya mudah dibawa dan digunakan saat bepergian.</span></p>', '4-kit-facial-ultra-light-sunscreen-quick-dry-moisturizing-sunscreen-spf-50', 55, '2024-10-15 13:10:00', '2025-01-04 11:09:21');
INSERT INTO `products` VALUES (22, 'Bhringraj Oil Hair Growth Serum Scalp Repair Hair Serum', '<p><strong>Sensasi Pemakaian:</strong></p><ul><li>minyak bhringraj membantu menutrisi kulit kepala dan rambut, meningkatkan pertumbuhan yang sehat dan mencegah rambut rontok.&nbsp;</li><li>Panthenol: Mencegah kerusakan akibat penyikatan dan penataan rambut yang kasar dengan melindungi serat rambut.&nbsp;</li><li>Minyak jojoba: Dapat melembabkan rambut dari dalam ke luar.&nbsp;<br>&nbsp;</li></ul><p><strong>Cara Pemakaian:</strong></p><ul><li>Semprotkan serum pada kulit kepala dan panjang rambut Anda.&nbsp;</li><li>Jalankan dengan jari atau sisir rambut agar merata.&nbsp;</li><li>Biarkan semalaman agar semua kebaikannya meresap.&nbsp;</li><li>Cuci rambut keesokan harinya. Gunakan 3 kali seminggu.</li></ul>', NULL, NULL, 7, 22, '<p><strong>Tekstur:</strong> Serum</p><p><strong>Jenis Kulit:</strong> Semua jenis rambut</p><p><strong>Digunakan untuk:</strong> Rambut</p><p><strong>Bahan Utama:</strong> <span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Bhringraj Oil, Panthenol, Jojoba Oil</span></p><p><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\"><strong>Manfaat: </strong>Memperbaiki ujung rambut bercabang. Minyak Bhringraj membantu menutrisi kulit kepala dan rambut, meningkatkan pertumbuhan yang sehat dan mencegah rambut rontok.</span></p>', 'bhringraj-oil-hair-growth-serum-scalp-repair-hair-serum', 80, '2024-10-15 13:10:00', '2025-01-05 01:30:24');
