INSERT INTO `products` VALUES (64, 'Pink AHA & BHA Skin Face Care Set', '<p><strong>Sensasi Pemakaian:</strong></p><ul><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">AHA BHA eksfoliasi kulit (mengangkat kotoran dan sel kulit mati)</span></li><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Ekstrak semangka pelembab&nbsp;</span></li></ul><p>&nbsp;</p><p><strong>Cara Pemakaian:&nbsp;</strong></p><ul><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">LANGKAH 1. Bersihkan kulit Anda dengan AHABHA Cleansing Foam dengan air hangat.&nbsp;</span></li><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">LANGKAH 2. Gunakan kapas untuk mengaplikasikan AHABHA Toner pada kulit dan tepuk-tepuk agar menyerap.&nbsp;</span></li><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">LANGKAH3. Gunakan pipet untuk mengaplikasikan AHABHA Serum dan pijat hingga menyerap.&nbsp;</span></li><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">LANGKAH4. Oleskan Cream AHABHA secara merata pada kulit.</span></li></ul>', NULL, NULL, 14, 45, '<p><strong>Tekstur:</strong>&nbsp;</p><p><strong>Jenis Kulit:</strong> Semua jenis kulit</p><p><strong>Digunakan untuk:</strong> Wajah</p><p><strong>Bahan Utama:</strong> <span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">AHA BHA&nbsp;</span></p><p><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\"><strong>Manfaat:</strong> Saksikan perbedaan nyata dari kulit kusam, bertekstur sebelum penggunaan, dan berjerawat menjadi kulit tampak lebih cerah dan halus pasca rejimen AHA &amp; BHA.</span></p>', 'pink-aha-bha-skin-face-care-set', 100, '2024-10-15 13:10:11', '2025-01-07 03:01:45');
INSERT INTO `products` VALUES (65, 'Facial Clay Mask Hydrate Face & Body, Shrink Pores, Anti-Acne Dead Sea Mud Mask', '<p><strong>Sensasi Pemakaian:</strong></p><ul><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Lumpur Laut Mati adalah penyedot debu bagi kulit. Ia mengandalkan viskositas dan adsorpsi yang tinggi untuk mengelupas kotoran jauh di dalam kulit, mengelupas jerawat, bekas noda, kemerahan dan jerawat di tubuh Anda. Ini membuka pori-pori wajah yang tersumbat untuk mendapatkan kulit wajah yang bersih dan lebih cerah.</span></li></ul><p>&nbsp;</p><p><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\"><strong>Cara Pemakaian:</strong></span></p><ul><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Setelah Oleskan masker tanah liat pada wajah, hindari mata dan bibir, biarkan selama 15-20 menit hingga kering lalu usap perlahan masker tanah liat dengan handuk basah atau bilas dengan air.&nbsp;</span></li><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Setelah membersihkan wajah, oleskan sedikit minyak wajah atau krim wajah untuk menjaga kelembapan wajah.</span></li></ul>', NULL, NULL, 8, 60, '<p><strong>Tekstur:</strong> Clay Mask</p><p><strong>Jenis Kulit:</strong> Semua jenis kulit</p><p><strong>Digunakan untuk:</strong> Wajah</p><p><strong>Bahan Utama:</strong> <span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Kaolin, Maris Limus, Hyaluronic Acid</span></p><p><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\"><strong>Manfaat:</strong> Membersihkan pori-pori, memurnikan, mengeluarkan minyak berlebih</span></p>', 'facial-clay-mask-hydrate-face-body-shrink-pores-anti-acne-dead-sea-mud-mask', 106, '2024-10-15 13:10:11', '2025-01-07 07:44:12');
INSERT INTO `products` VALUES (66, 'Customized Beard Care Foam Shave Cream Moisturizing Post Shave Lotion Soothing Shaving Cream for Men', '<p><strong>Sensai Pemakaian:</strong></p><ul><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Minyak pohon teh Efek Antibakteri, Antimikroba dan Antiseptik.&nbsp;</span></li><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Lidah buaya Menenangkan kulit yang gatal dan meredakan rasa gatal sepanjang hari.&nbsp;</span></li><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Sea Buckthorn Melawan bau tak sedap dan mengurangi iritasi kulit.</span></li><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">&nbsp;Vitamin E Ini melembabkan kulit dan menjaganya tetap terhidrasi sepanjang hari.&nbsp;</span></li><li><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Ekstrak Calendula Mencegah infeksi seperti kurap, gatal di selangkangan, dan lecet.</span></li></ul><p><strong>Cara Pemakaian:</strong></p><p><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Saat masuk atau keluar dari kamar mandi, oleskan sedikit ke tangan yang bersih dan lembap. Gosok bersama dan oleskan ke kulit intim. Bilas hingga bersih dengan air hangat.</span></p>', NULL, NULL, 14, 47, '<p><strong>Tekstur: </strong>Cream</p><p><strong>Jenis Kulit:</strong> Semua Jenis Kulit</p><p><strong>Digunakan Untuk:</strong> <span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">intimate skin area</span></p><p><strong>Bahan Utama: </strong><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Aloe, Tea Tree Oil, Vitamin E</span></p><p><strong>Manfaat: </strong><span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">Kami menggabungkan lidah buaya organik dengan ekstrak tumbuhan yang menenangkan untuk membersihkan area intim tempat bakteri paling mungkin tumbuh dan mengiritasi.</span></p>', 'customized-beard-care-foam-shave-cream-moisturizing-post-shave-lotion-soothing-shaving-cream-for-men', 93, '2024-10-15 13:10:11', '2025-01-06 04:31:38');
